create table question_list
(
    qid         int auto_increment comment '题库唯一id',
    type        int          not null comment '试题类型 1单选，2多选，3其他',
    question    varchar(300) not null comment '问题',
    options     json         not null comment '选项：json类型，因为可能有多个选项或者其他格式的
[]',
    answer      json         not null comment '答案',
    questionImg blob         null comment '图片',
    constraint question_list_qid_uindex
        unique (qid)
)
    comment '题库';

alter table question_list
    add primary key (qid);

