INSERT INTO examhepler.question_list (qid, type, question, options, answer, questionImg) VALUES (35, 1, '请参见图示。管理员在 R1 上配置了标准 ACL，并将其应用在串行 S0/0/0 接口的出站方向上。如果从S0/0/0接口发出的流量与配置的 ACL 语句不匹配，会发生什么情况？', '[{"optionName": "A", "optionValue": "具体的行为取决于目的IP地址。"}, {"optionName": "B", "optionValue": "流量会被丢弃。"}, {"optionName": "C", "optionValue": "检查源IP地址，如果没有找到匹配项，流量会从S0/0/1接口路由出去。"}, {"optionName": "D", "optionValue": "具体的行为取决于目的IP地址和端口号。"}]', '["B"]', 0x);
INSERT INTO examhepler.question_list (qid, type, question, options, answer, questionImg) VALUES (36, 2, '请参见图示。两台路由器的千兆接口都已配置了与其相连的 VLAN 编号相匹配的子接口编号。VLAN 10 上的 PC 应该可以打印内容到 VLAN 12 上的 P1 打印机。VLAN 20 上的 PC 应该打印内容到 VLAN 22 上的打印机。您应该使用什么接口、在什么方向放置一个标准的 ACL，使其允许从数据 VLAN 10 打印到 P1，但阻止 VLAN 20 上的 PC 使用 P1 打印机？（请选择两项。）', '[{"optionName": "A", "optionValue": "出站"}, {"optionName": "B", "optionValue": "R1 Gi0/1.12"}, {"optionName": "C", "optionValue": "R2 Gi0/1.20"}, {"optionName": "D", "optionValue": "入站"}, {"optionName": "E", "optionValue": "R2 S0/0/1"}, {"optionName": "F", "optionValue": "R1 S0/0/0"}]', '["A", "B"]', 0x);
